package frc.robot.commands;

import edu.wpi.first.wpilibj2.command.CommandBase;
import frc.robot.Constants;
import frc.robot.subsystems.driveSystem;

public class leaveTarmac extends CommandBase {
        private final driveSystem m_driveSystem;
        private final double m_distanceInches;
        private double initalPosition;
        private double finalPosition;

    public leaveTarmac(driveSystem subsystem, double distanceInches) {
        m_driveSystem = subsystem;
        m_distanceInches = distanceInches;
        addRequirements(m_driveSystem);
    }

    @Override
    public void initialize() {
        initalPosition = m_driveSystem.getSensor();
        finalPosition = initalPosition-2048*Constants.driveConstants.gearboxRatio*m_distanceInches/(6*Math.PI);
    }

    @Override
    public void execute() {
        m_driveSystem.arcadeDrive(-0.5, 0);
    }

    @Override
    public void end(boolean interrupted) {
        m_driveSystem.arcadeDrive(0, 0);
    }

    @Override
    public boolean isFinished() {
        return m_driveSystem.getSensor() < finalPosition;
    }

    @Override
    public boolean runsWhenDisabled() {
        return false;
    }
}
